import React from 'react';
import './App.scss';

import Card from "./components/card/SymbolCard"
import Axios from 'axios';

class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = { symbol: '', cards: {} };

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({ symbol: event.target.symbol });
    }

    handleSubmit(event) {
      // Axios.get(`https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=${this.state.value}&apikey=HY0JP87WH3PG17X6`)
      Axios.get(`https://www.alphavantage.co/query?function=TIME_SERIES_DAILY&symbol=${this.state.symbol}&apikey=HY0JP87WH3PG17X6`)
      .then((response) => {
        // handle success
        console.log(response.data);
        var cards = this.state.cards
        cards[this.state.symbol] = response.data
        this.setState({ cards: cards });
      })
      .catch((error) => {
        // handle error
        console.log(error);
      })
      .finally(function () {
        console.log('')
      });
      event.preventDefault()
    }

    render() {
      return (
        <div className="container">
          <h1>Stock Watcher</h1>
          <form className="form" onSubmit={this.handleSubmit}>
            <input type="text" value={this.state.symbol} onChange={this.handleChange} />
            <button type="sumbit" value="Submit" className="add shadow">ADD STOCK</button>
          </form>
          <div className="card-container">
            <Card trending="up"></Card>
            <Card trending="up"></Card>
            <Card trending="down"></Card>
            <Card trending="down"></Card>
            <Card trending="up"></Card>
            <Card trending="up"></Card>
          </div>
        </div>
      );
    }

}

export default App;
