import React from 'react';
import './App.scss';

import Card from "./components/card/SymbolCard"
import Search from './components/search/Search'
import Axios from 'axios';

class App extends React.Component {
    constructor(props) {
      super(props);
      this.state = { value: '' };

      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
      this.setState({ value: event.target.value });
    }

    handleSubmit(event) {
      console.log(this)
      Axios.get(`https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=${this.state.value}&apikey=HY0JP87WH3PG17X6`)
      .then(function (response) {
        // handle success
        console.log(response);
        // this.setState({ value: event.target.value });
      })
      .catch(function (error) {
        // handle error
        console.log(error);
      })
      .finally(function () {
        console.log('')
      });
      event.preventDefault()
    }

    render() {
      return (
        <div className="container">
          <h1>Stock Watcher</h1>
          <Search handleSubmit={this.handleSubmit}></Search>
          <div className="card-container">
            <Card trending="up"></Card>
            <Card trending="up"></Card>
            <Card trending="down"></Card>
            <Card trending="down"></Card>
            <Card trending="up"></Card>
            <Card trending="up"></Card>
          </div>
        </div>
      );
    }

}

export default App;
